+      document.getElementById('dashboardBody').appendChild(tr);
+    });
+
+    // Show edit modal
+    function showEditForm() {
+      const user = JSON.parse(localStorage.getItem('loggedInUser'));
+      document.getElementById('editFirstName').value = user.firstName;
