+/* ========== CloudClips Theme Enhancements ========== */
+:root{
+  --bg: #0f1220;
+  --surface: #151a2d;
+  --surface-2: #1b2140;
+  --text: #e8ecff;
+  --muted: #a9b1d6;
+  --primary: #7c5cff;
+  --primary-2: #4fb3ff;
+  --accent: #00d4ff;
+  --success: #27c93f;
+  --danger: #ff5573;
+  --warn: #ffb020;
+  --shadow: 0 10px 30px rgba(0,0,0,.35);
+}
+
+*{box-sizing:border-box}
+html,body{
+  margin:0;
+  padding:0;
+  background: radial-gradient(1200px 800px at 80% -10%, rgba(124,92,255,.15), transparent 60%),
+              radial-gradient(1000px 700px at -10% 20%, rgba(79,179,255,.12), transparent 60%),
+              var(--bg);
+  color:var(--text);
+  font-family: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Inter, "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji", "Segoe UI Emoji";
+  line-height:1.6;
+  scroll-behavior:smooth;
+}
+
+/* Container */
+.container{width:min(1100px, 92%); margin:0 auto;}
+
+/* Navbar */
+.navbar{
+  position:sticky; top:0; z-index:50;
+  backdrop-filter: saturate(160%) blur(8px);
+  background: linear-gradient( to bottom, rgba(10,12,24,.9), rgba(10,12,24,.6) );
+  border-bottom: 1px solid rgba(255,255,255,.06);
+}
+.navbar-inner{ display:flex; align-items:center; justify-content:space-between; padding:14px 0; }
+.brand{
+  display:flex; align-items:center; gap:10px; font-weight:800; letter-spacing:.3px;
+  text-decoration:none; color:var(--text); font-size:1.1rem;
+}
+.brand-badge{
+  display:inline-grid; place-items:center; width:34px;height:34px; border-radius:10px;
+  background: linear-gradient(135deg, var(--primary), var(--accent));
+  box-shadow: 0 8px 18px rgba(124,92,255,.35), inset 0 1px 0 rgba(255,255,255,.2);
+  font-weight:900;
+}
+.nav-actions{ display:flex; align-items:center; gap:10px; }
+.theme-toggle, .btn{
+  border:1px solid rgba(255,255,255,.1);
+  background: linear-gradient(180deg, var(--surface), var(--surface-2));
+  color:var(--text);
+  padding:10px 14px; border-radius:12px; cursor:pointer;
+  transition: transform .12s ease, background .3s ease, border-color .3s ease, box-shadow .3s ease;
+  box-shadow: 0 6px 16px rgba(0,0,0,.25);
+}
+.theme-toggle:hover, .btn:hover{ transform: translateY(-1px); border-color: rgba(255,255,255,.2); }
+.btn:active{ transform: translateY(0); }
+
+/* Buttons */
+.btn-primary{
+  background: linear-gradient(135deg, var(--primary), var(--primary-2));
+  border: none;
+}
+.btn-outline{ background: transparent; border:1px solid rgba(255,255,255,.18); }
+.btn-ghost{ background: transparent; border:none; }
+.btn-pill{ border-radius:999px; padding:10px 18px; }
+
+/* Cards */
+.card{
+  background: linear-gradient(180deg, rgba(255,255,255,.04), rgba(255,255,255,.02));
+  border:1px solid rgba(255,255,255,.08);
+  border-radius:18px; padding:18px;
+  box-shadow: var(--shadow);
+  transition: transform .2s ease, box-shadow .2s ease, border-color .2s ease;
+}
+.card:hover{ transform: translateY(-3px); border-color: rgba(255,255,255,.15); }
+
+/* Inputs */
+.input, input[type="text"], input[type="email"], input[type="password"], input[type="file"], textarea, select{
+  width:100%; padding:12px 14px; border-radius:12px;
+  background: linear-gradient(180deg, var(--surface), var(--surface-2));
+  border:1px solid rgba(255,255,255,.1); color:var(--text);
+  outline:none; transition: border-color .2s ease, box-shadow .2s ease;
+}
+.input:focus, input:focus, textarea:focus, select:focus{
+  border-color: var(--primary); box-shadow: 0 0 0 3px rgba(124,92,255,.25);
+}
+
+/* Grid */
+.grid{ display:grid; gap:16px; }
+.grid-2{ grid-template-columns: repeat(2, minmax(0,1fr)); }
+.grid-3{ grid-template-columns: repeat(3, minmax(0,1fr)); }
+@media (max-width: 900px){ .grid-3{ grid-template-columns: 1fr 1fr; } }
+@media (max-width: 640px){ .grid-2, .grid-3{ grid-template-columns: 1fr; } }
+
+/* Hero */
+.hero{
+  padding:64px 0 34px;
+}
+.hero h1{ font-size: clamp(1.8rem, 4vw, 2.4rem); line-height:1.2; margin:0 0 10px; }
+.hero p{ color: var(--muted); margin:0 0 18px; }
+
+/* Footer */
+.footer{
+  margin-top:40px; padding:22px 0; text-align:center;
+  border-top: 1px solid rgba(255,255,255,.08);
+  color:var(--muted);
+}
+
+/* Reveal Animations */
+.reveal{ opacity:0; transform: translateY(16px) scale(.98); transition: opacity .6s ease, transform .6s cubic-bezier(.2,.7,.1,1); }
+.reveal.show{ opacity:1; transform: none; }
+
+/* Hover Zoom for media tiles */
+.media-tile{
+  position:relative; overflow:hidden; border-radius:16px;
+  aspect-ratio: 1 / 1.2; background: #0a0d1c;
+  border:1px solid rgba(255,255,255,.06);
+}
+.media-tile img, .media-tile video{
+  width:100%; height:100%; object-fit:cover; display:block;
+  transition: transform .5s ease;
+}
+.media-tile:hover img, .media-tile:hover video{ transform: scale(1.05); }
+
+/* Link */
+a{ color: var(--primary-2); text-decoration:none; }
+a:hover{ text-decoration:underline; }
+
+/* Utility */
+.mt-2{ margin-top:8px } .mt-4{ margin-top:16px } .mt-6{ margin-top:24px }
+.mb-2{ margin-bottom:8px } .mb-4{ margin-bottom:16px } .mb-6{ margin-bottom:24px }
+
+/* Dark/Light toggle support */
+.light body, body.light{
+  background: #f6f7fb;
+  color:#0b0e1a;
+}
+.light .navbar{ background: rgba(255,255,255,.8); border-bottom: 1px solid rgba(0,0,0,.06); }
+.light .card{ background: white; border-color: rgba(0,0,0,.08); }
+.light .btn, .light .input{ background:white; color:#0b0e1a; border-color: rgba(0,0,0,.1); }
+.light a{ color:#2d5fff; }
+.light .media-tile{ border-color: rgba(0,0,0,.08); }

