+/* Clean Classic Instagram Clone Theme */
+
+body {
+  font-family: Arial, sans-serif;
+  background-color: #f5f5f5;
+  color: hsl(212, 100%, 51%);
+  margin: 0;
+  padding: 0;
+}
+
+.container {
+  width: 90%;
+  max-width: 800px;
+  margin: 0 auto;
+}
+
+header {
+  background-color: #2e4dff;
+  color: white;
+  padding: 15px 0;
+  text-align: center;
+}
+
+header h1 {
+  margin: 0;
+  font-size: 1.6em;
+}
+
+header nav {
+  margin-top: 10px;
+}
+
+header nav a {
+  color: white;
+  text-decoration: none;
+  margin: 0 12px;
+  font-weight: bold;
+}
+
+header nav a:hover {
+  text-decoration: underline;
+}
+
+main {
+  margin-top: 30px;
+}
+
+form {
+  background-color: white;
+  padding: 20px;
+  border-radius: 8px;
+  margin-bottom: 30px;
+  box-shadow: 0 0 5px rgba(0, 0, 0, 0.1);
+}
+
+input, textarea {
+  width: 100%;
+  margin-bottom: 15px;
+  padding: 10px;
+  font-size: 1rem;
+  border: 1px solid #ccc;
+  border-radius: 4px;
+}
+
+input[type="file"] {
+  padding: 6px;
+}
+
+button {
+  padding: 10px 15px;
+  background-color: #333;
+  color: white;
+  border: none;
+  border-radius: 4px;
+  font-weight: bold;
+  cursor: pointer;
+}
+
+button:hover {
+  background-color: #555;
+}
+
+.feed {
+  display: flex;
+  flex-direction: column;
+  gap: 20px;
+}
+
+.post {
+  background-color: white;
+  border-radius: 10px;
+  padding: 15px;
+  box-shadow: 0 0 10px rgba(0,0,0,0.05);
+}
+
+.post h3 {
+  margin-bottom: 10px;
+  font-size: 1.2rem;
+}
+
+.post img,
+.post video {
+  width: 100%;
+  border-radius: 6px;
+  margin-bottom: 10px;
+}
+
+.post p {
+  margin: 0 0 10px;
+}
+
+.actions {
+  display: flex;
+  justify-content: start;
+  gap: 15px;
+  font-size: 1.2rem;
+}
+
+.actions span {
+  cursor: pointer;
+}
+
+.actions span:hover {
+  opacity: 0.7;
+}
+
+footer {
+  background-color: #2e4dff;
+  color: white;
+  text-align: center;
+  padding: 10px 0;
+  margin-top: 50px;
+  font-size: 0.9em;
+}
+
+/* Responsive */
+@media (max-width: 600px) {
+  header nav {
+    display: flex;
+    flex-direction: column;
+    gap: 8px;
+  }
+}
+/* Container for the entire dashboard */
+.dashboard-container {
+  max-width: 1200px;
+  margin: 20px auto;
+  padding: 0 15px;
+  box-sizing: border-box;
+}
+
+/* Table styling */
+.dashboard-table {
+  width: 100%;
+  border-collapse: collapse;
+  margin-bottom: 30px;
+  font-family: Arial, sans-serif;
+}
+
+/* Header row */
+.dashboard-table thead {
+  background-color: #2c3e50;
+  color: #001529;
+}
+
+.dashboard-table th {
+  padding: 12px;
+  text-align: left;
+  font-size: 1rem;
+  border-bottom: 2px solid #34495e;
+}
+
+/* Body rows */
+.dashboard-table tbody tr {
+  border-bottom: 1px solid #ffff;
+}
+
+.dashboard-table tbody tr:nth-child(even) {
+  background-color: #001529;
+}
+
+.dashboard-table td {
+  padding: 12px;
+  vertical-align: top;
+  font-size: 0.9rem;
+}
+
+/* Actions column (e.g., edit, delete buttons) */
